import React, { useState } from 'react';

export default function TextForm(props) {
    const handleLowClick = ()=> {
        console.log('Lower case button was clicked' + text);
        let newText = text.toLowerCase();
        setText(newText)
    }

    const handleUpClick = ()=> {
        console.log('Upper case button was clicked' + text);
        let newText = text.toUpperCase();
        setText(newText)
    }
    
    const handleOnChange = (event)=> {
        console.log('Handling on change')
        setText(event.target.value)
    }

    const [text, setText] = useState('Please Enter Text Here');
    return (
        <>
            <div className="container">
                <form>
                    <h1>{props.heading}</h1>
                    <div className="form-group">
                        <textarea className="form-control" value={text} onChange={handleOnChange} id="mybox" rows="8"></textarea>
                    </div>
                    <button className="btn btn-primary" onClick={handleUpClick}>Convert To Uppercase</button>
                    <button className="btn btn-primary ml-3" onClick={handleLowClick}>Convert To Lowercase</button>
                </form>
            </div>
            <div className="container">
                <h1>Your text Summary</h1>
                <p></p>
            </div>
        </>
  )
}
