import React, { useState } from 'react';

export default function TextForm(props) {
    const handleAlternate = ()=> {
        var chars = text.toLowerCase().split("");
        for (var i = 0; i < chars.length; i += 2) {
          chars[i] = chars[i].toUpperCase();
        }
        setText(chars.join(""));
    }

    const handleClear = ()=> {
        console.log('Clear Text button was clicked' + text);
        let newText = '';
        setText(newText)
    }

    const handleLowClick = ()=> {
        console.log('Lower case button was clicked' + text);
        let newText = text.toLowerCase();
        setText(newText)
    }

    const handleUpClick = ()=> {
        console.log('Upper case button was clicked' + text);
        let newText = text.toUpperCase();
        setText(newText)
    }
    
    const handleOnChange = (event)=> {
        console.log('Handling on change')
        setText(event.target.value)
    }

    const [text, setText] = useState('');
    return (
        <>
            <div className="container" style={{backgroundColor: props.mode==='light'?'white':'white',color:props.mode==='dark'?'white':'black'}}>
                <form>
                    <h1>{props.heading}</h1>
                    <div className="form-group">
                        <textarea className="form-control" value={text} onChange={handleOnChange} id="mybox" rows="8"></textarea>
                    </div>
                    <button type="button" className="btn btn-primary" onClick={handleUpClick}>Convert To Uppercase</button>
                    <button type="button" className="btn btn-primary ml-1" onClick={handleLowClick}>Convert To Lowercase</button>
                    <button type="button" className="btn btn-primary ml-1" onClick={handleClear}>Clear Text</button>
                    <button type="button" className="btn btn-primary ml-1" onClick={handleAlternate}>Alternating Text</button>

                </form>
            </div>
            <div className="container">
                <h2 className="mt-5">Your text Summary</h2>
                <p className="font-weight-bold mb-0">The text is</p>
                <p>{text}</p>
                <p className="font-weight-bold mb-0">Character count is</p>
                <p>{text.split(" ").length} Words and {text.length} Characters</p>
                <p className="font-weight-bold mb-0">Time required to read</p>
                <p>{0.008 * text.split(" ").length} Minutes Read</p>
            </div>
        </>
    )
}
